package com.andyadc.foundation.util;import com.idea4j.framework.FrameworkException;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * Object utility * * @author andaicheng */public class ObjectUtil {    private static final Logger LOGGER = LoggerFactory.getLogger(ObjectUtil.class);    private ObjectUtil() {    }    /**     * Create the instance by reflection     */    @SuppressWarnings("unchecked")    public static <T> T newInstance(String className) {        T instance;        try {            Class<?> clazz = ClassUtil.loadClass(className);            instance = (T) clazz.newInstance();        } catch (Exception e) {            LOGGER.error("Create instance error!", e);            throw new FrameworkException(e);        }        return instance;    }}